import UIKit

class HomePresenter {
    
    private var view: HomeViewProtocol?
    var interactor: HomeInteractorProtocol?
    private let router: HomeRouterProtocol
    
    init(interface: HomeViewProtocol, interactor: HomeInteractorProtocol?, router: HomeRouterProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }

}

extension HomePresenter: HomePresenterProtocol {

    func didLoad() {
        self.view?.showLoading(show: true)
        self.interactor?.loadCountries()
    }
    
    func didRetrieveCountries(countries: [CountryEntity]) {
        self.view?.showCountries(countries: countries)
    }
    
    func didRetrieveError() {
        
    }
    
    func didSelectedCountry(country: CountryEntity) {
        self.router.showCountryDetails(country: country)
    }
    
    
}
