import UIKit

class HomeRouter: BaseRouter {
    
    static func makeController() -> HomeTableViewController {
        
        let storyBoad = UIStoryboard(name: "MainInterface", bundle: nil)
        let view = storyBoad.instantiateViewController(withIdentifier: "HomeTableViewController") as! HomeTableViewController
        let interactor = HomeInteractor()
        let router = HomeRouter(viewController: view)
        let presenter = HomePresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        
        return view
    }
}

extension HomeRouter: HomeRouterProtocol {
    func showCountryDetails(country: CountryEntity) {
        self.pushModule(viewController: DetailsCountryRouter.makeController(parameter: ParameterIO(withObject: country)), animated: true)
    }
}
